//import React from 'react';

const getQuestionsForTopic = (topic) => {
    const questions = {
      Geography: [
        {
            questionText: "1. What is the capitol of Afghanistan?",
            options: [
              'Tehran',
              'Kabul',
              'Dushanbe',
              'Tashkent'
            ],
            correctAnswer: 'Kabul',
          },
          {
            questionText: '2. What continent is Suriname in?',
            options: [
              'Africa',
              'Europe',
              'Asia',
              'South America'
            ],
            correctAnswer: 'South America',
          },
          {
            questionText: '3. What is the capitol of Trinidad and Tobago?',
            options: [
              'Port of Spain',
              'Kingston',
              'Toronto',
              'E=mc^2'
            ],
            correctAnswer: 'Port of Spain',
          },
          {
            questionText: '4. Which of the following countries is NOT in Asia?',
            options: [
              'Uzbekistan',
              'Armenia',
              'Thailand',
              'Algeria'
            ],
            correctAnswer: 'Algeria',
          },
          {
            questionText: '5. What is the capitol of Krygyzstan',
            options: [
              'Tashkent',
              'Bishkek',
              'Astana',
              'Dubai'
            ],
            correctAnswer: 'Bishkek',
          },
      ],
      Math: [
        {
            questionText: "1. What are the two national languages of Afghanistan?",
            options: [
              'Farsi and Pashto',
              'Arabic and Farsi',
              'Pashto and Urdu/Hindi',
              'Uzbek and Balochi'
            ],
            correctAnswer: 'Farsi and Pashto',
          },
          {
            questionText: 'In what language family (of the following) is English?',
            options: [
              'Germanic',
              'Romance',
              'Semitic',
              ''
            ],
            correctAnswer: '4/5',
          },
          {
            questionText: 'A bag contains 4 red balls and 3 black balls. If one ball is drawn at random, find the probability that it is red.',
            options: [
              '4/7',
              '3/7',
              '1/7',
            ],
            correctAnswer: '4/7',
          },
          {
            questionText: 'If an alphabet is selected randomly in English, find the probability that it is a consonant.',
            options: [
              '5/26',
              '21/26',
              '12/26',
            ],
            correctAnswer: '21/26',
          },
          {
            questionText: 'In a survey of 200 children, it was found that 142 likes outdoor games while 58 liked indoor games. Find the probability that a child chosen at random likes indoor games,',
            options: [
              '0.30',
              '0.29',
              '0.31',
            ],
            correctAnswer: '0.29',
          },
      ],
    };

    return questions[topic] || [];
  };

  export default getQuestionsForTopic;
